import axios from "axios";

export async function loadPokedata() {
  let result;
  await axios
    .get("https://pokeapi.co/api/v2/pokemon/?limit=10&offset=10")
    .then((res) => (result = res.data));
  console.log("in api", result);
  return result;
}
export const indiPoke = async (pokemon) => {
    let result;
    // console.log("in indiURL", pokemon.url);
    await axios
      .get(pokemon.url)
      .then((res) => {
        return res.data.sprites.front_shiny;
      })
      .then();
    // console.log("in indiPOKE", result);
