import React, { useState, useEffect } from "react";

import { loadPokedata } from "../api";
import { indiPoke } from "../api";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  title: {
    padding: theme.spacing(0),
    textAlign: "center",
    color: theme.palette.text.secondary,
    height: "200px",
    top: "opx",
    objectFit: "cover",
    overflow: "hidden",
  },
}));
const getPokeData = async () => {
  const data = await loadPokedata();
  console.log("hello", data);
  return data;
};

const PokeDash = () => {
  const classes = useStyles();
  const [state, setState] = useState({
    pokeData: "",
  });
  useEffect(async () => {
    const pdata = await getPokeData();
    setState({ pokeData: pdata.results });
  }, []);

  const pokeCard = () => {
    const pdata = state.pokeData;
    console.log("pdata", pdata);

    return pdata.map((pokemon, i) => {
      let url;
      const s = async (pokemon) => {
        url = await indiPoke(pokemon);
        console.log("url is", url);
      };
      s(pokemon);

      return (
        <Grid item xs={3}>
          <Paper className={classes.paper}>{pokemon.name}</Paper>
          <img id={url}></img>;
        </Grid>
      );
    });
  };

  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Paper className={classes.title}>
          <img
            src="http://www.pngnames.com/files/5/Pokemon-Logo-PNG-Free-Pic.png"
            width="500px"
            height="100%"
            style={{}}
          />
        </Paper>
      </Grid>

      {console.log("state", state.pokeData)}
      {state.pokeData && pokeCard()}
    </Grid>
  );
};

export default PokeDash;
