import React, { useState, useEffect } from "react";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import { indiPoke, getPokemon } from "../api";
import TextField from "@material-ui/core/TextField";
import Portal from '@material-ui/core/Portal';
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  title: {
    padding: theme.spacing(0),
    textAlign: "center",
    color: theme.palette.text.secondary,
    height: "200px",
    top: "opx",
    objectFit: "cover",
    overflow: "hidden",
  },
}));
export const PokeCard = (props) => {
  const container = React.useRef(null);
  const classes = useStyles();
  const [state, setState] = useState({
    url: "",
    correct:false,
  });
  useEffect(() => {
    const somefunction = async () => {
      const purl = props.poke;

      if (purl) {
        const url = await indiPoke(purl);
        setState({ url: url });
      }
    };
    somefunction();
  }, [props]);

  const handleChange=(e,v)=>{
   if(v==props.poke.name){
    setState({ correct: true });
   }
  }
  return (
    <Grid item xs={3}>
      <Paper className={classes.paper}>
        <img src={state.url}></img>
        <TextField id="standard-basic" label="Guess the pokemon" onChange={handleChange} />
       { state.correct && <Portal container={container.current}>
            <span>But I actually render here!</span>
          </Portal> }
      </Paper>
    </Grid>
  );
};
