import React, { useState, useEffect } from "react";

import { loadPokedata } from "../api";
import { indiPoke, getPokemon } from "../api";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import axios from "axios";
import { PokeCard } from "./PokeCard";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  title: {
    padding: theme.spacing(0),
    textAlign: "center",
    color: theme.palette.text.secondary,
    height: "200px",
    top: "opx",
    objectFit: "cover",
    overflow: "hidden",
  },
}));
const getPokeData = async () => {
  const data = await loadPokedata();

  return data;
};

const PokeDash = () => {
  const classes = useStyles();
  const [state, setState] = useState({
    pokeData: "",
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [pokePerPage, setPokePerPage] = useState(12);
  useEffect(async () => {
    const pdata = await getPokeData();
    setState({ pokeData: pdata.results });
  }, []);

  const imageurl = async (pokemon) => {
    let url;
    url = await indiPoke(pokemon);
    return url;
  };

 //get current poke
 const indexOfLastPoke= currentPage * pokePerPage
 const indexOfFirstPoke= indexOfLastPoke


  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Paper className={classes.title}>
          <img
            src="http://www.pngnames.com/files/5/Pokemon-Logo-PNG-Free-Pic.png"
            width="500px"
            height="100%"
            style={{}}
          />
        </Paper>
      </Grid>

      {state.pokeData &&
        state.pokeData.map((pokemon) => {
          return <PokeCard poke={pokemon} />;
        })}
    </Grid>
  );
};

export default PokeDash;
